```
Parent <- R6::R6Class(
  "Parent",
  lock_objects = FALSE,
  public = list(
    initialize = function() {
      self[["foo"]] <- function() {
        print('foo')
      }
    }
  )
)

Child <- R6::R6Class(
  "Child",
  inherit = Parent,
  lock_objects = FALSE,
  public = list(
    foo = function() {
      super$foo()
    }
  )
)

child <- Child$new()
#> Error in self$foo <- function() { : 
